INSERT INTO met_column (id, name, foldername, filename, bigclass, samefile, module, no_order, wap_ok, wap_nav_ok, if_in, nav, ctitle, keywords, content, description, other_info, custom_info, list_order, new_windows, classtype, out_url, index_num, access, indeximg, columnimg, isshow, lang, namemark, releclass, display, icon, nofollow, text_size, text_color, thumb_list, thumb_detail, list_length, tab_num, tab_name) VALUES (132, '公司简介', 'about', '', 126, 0, 1, 0, 0, 0, 0, 0, '', '', '<p microsoft=\"\" segoe=\"\" helvetica=\"\" apple=\"\" color=\"\" ui=\"\" noto=\"\" white-space:=\"\" background-color:=\"\">长沙米拓信息技术有限公司成立于 2009 年 6 月，是一家专注于「为中小企业提供信息化服务」的高新技术企业、双软认证企业。</p><p microsoft=\"\" segoe=\"\" helvetica=\"\" apple=\"\" color=\"\" ui=\"\" noto=\"\" white-space:=\"\" background-color:=\"\">公司一直围绕互联网相关软件进行自主开发和运营，旗下主打产品平台有：<a href=\"https://www.mituo.cn/\" target=\"_blank\" title=\"米拓建站\" textvalue=\"米拓建站\">米拓建站</a>、<a href=\"https://dy.mituo.cn/\" target=\"_blank\" title=\"米拓单页\">米拓单页制作平台</a>、<a href=\"https://www.mituo.cn/\" target=\"_blank\" title=\"米拓流程\">米拓流程管理系统</a>。</p><p microsoft=\"\" segoe=\"\" helvetica=\"\" apple=\"\" color=\"\" ui=\"\" noto=\"\" white-space:=\"\" background-color:=\"\"><br style=\"box-sizing: border-box;\"/></p><p microsoft=\"\" segoe=\"\" helvetica=\"\" apple=\"\" color=\"\" ui=\"\" noto=\"\" white-space:=\"\" background-color:=\"\">米拓<a title=\"建站\" target=\"_blank\" href=\"https://www.mituo.cn/\">建站</a>：</p><p microsoft=\"\" segoe=\"\" helvetica=\"\" apple=\"\" color=\"\" ui=\"\" noto=\"\" white-space:=\"\" background-color:=\"\">与其他<a title=\"建站公司\" target=\"_blank\" href=\"https://www.mituo.cn/\">建站公司</a>不同的是，我们自创业之初就自主研发了一款免费开源的企业级 CMS ——米拓企业建站系统（MetInfo ），并且以 MetInfo 为核心产品一直不断更新研发至今，致力于打造中小企业优质的互联网信息化工具供应平台。</p><p microsoft=\"\" segoe=\"\" helvetica=\"\" apple=\"\" color=\"\" ui=\"\" noto=\"\" white-space:=\"\" background-color:=\"\">米拓企业建站系统的愿景是任何中小企业和个人能够轻松基于 MetInfo 搭建高品质的企业门户网站（不需要任何专业技能）；我们不夹杂当前网站建设行业乱象，提供「清澈透明实惠」的价格和优质的售后服务。</p><p microsoft=\"\" segoe=\"\" helvetica=\"\" apple=\"\" color=\"\" ui=\"\" noto=\"\" white-space:=\"\" background-color:=\"\"><br style=\"box-sizing: border-box;\"/></p><p microsoft=\"\" segoe=\"\" helvetica=\"\" apple=\"\" color=\"\" ui=\"\" noto=\"\" white-space:=\"\" background-color:=\"\">米拓单页：</p><p microsoft=\"\" segoe=\"\" helvetica=\"\" apple=\"\" color=\"\" ui=\"\" noto=\"\" white-space:=\"\" background-color:=\"\">米拓单页制作平台是专题页、落地页、米拓企业建站系统简洁版、H5、在线表单（如在线报名、投票、调查、考试等）的综合体。用户使用米拓单页，一套页面数据，就可解决所有宣传推广落地页，并可以自适应电脑、手机、微信、QQ、自媒体浏览，还可以可部署到你自己的服务器。</p><p microsoft=\"\" segoe=\"\" helvetica=\"\" apple=\"\" color=\"\" ui=\"\" noto=\"\" white-space:=\"\" background-color:=\"\"><br style=\"box-sizing: border-box;\"/></p><p microsoft=\"\" segoe=\"\" helvetica=\"\" apple=\"\" color=\"\" ui=\"\" noto=\"\" white-space:=\"\" background-color:=\"\">米拓流程：</p><p microsoft=\"\" segoe=\"\" helvetica=\"\" apple=\"\" color=\"\" ui=\"\" noto=\"\" white-space:=\"\" background-color:=\"\">米拓流程管理系统是在线审批系统、客服工单系统、OA系统、协同办公系统的综合体，用于解决企事业单位或团队的对外服务和内部管理及协同办公的流程自动化。</p><p microsoft=\"\" segoe=\"\" helvetica=\"\" apple=\"\" color=\"\" ui=\"\" noto=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\" style=\"text-align:center;\"><img src=\"../upload/202109/1631529783323522.jpg\" data-width=\"800\" width=\"800\" data-height=\"600\" height=\"600\" title=\"网站建设,网站制作,cms,企业建站,建站系统\" alt=\"模板建站\" style=\"box-sizing: border-box; vertical-align: top; border-style: none; object-fit: cover; max-width: 100%; height: auto !important; display: inline-block;\"/></p><p microsoft=\"\" segoe=\"\" helvetica=\"\" apple=\"\" color=\"\" ui=\"\" noto=\"\" white-space:=\"\" background-color:=\"\" text-align:=\"\" style=\"text-align:center;\"><br/></p><p microsoft=\"\" segoe=\"\" helvetica=\"\" apple=\"\" color=\"\" ui=\"\" noto=\"\" white-space:=\"\" background-color:=\"\">我们秉承“为合作伙伴创造价值”的核心价值观，并以“诚实、宽容、创新、服务”为企业精神，通过自主创新和真诚合作为电子商务及信息服务行业创造价值。</p><p microsoft=\"\" segoe=\"\" helvetica=\"\" apple=\"